// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "./ComptrollerInterface.sol";

interface GoodInterface {
    enum Error {
        NO_ERROR,
        INVALID_PARAMETER,
        INVALID_STATUS
    }

    enum Stage {
        NONE,
        INIT,
        CAN_JOIN,
        LOCKED,
        IN_PICK,
        FINISHED,
        FAILED
    }

    struct UserRecord {
        address user;
        uint256 count;
        bool isJoined;
        bool isPaybacked;
        uint joinTime;
        uint joinBlockNum;
    }

    struct UserJoinRecord {
        address user;
        uint startIndex;
        uint256 count;
        uint joinTime;
        uint joinBlockNum;
    }

    struct WinnerTime {
        uint blockNum;
        uint blockTime;
        uint index;
    }

    event JoinSuccessful(address user, uint256 amount, uint32 goodId);
    event LotteryComplete(
        address user,
        uint256 totalBalance,
        uint256 priceValue,
        uint256 mf,
        uint256 burned,
        uint256 paybackUnit
    );
}

contract GoodStorage is GoodInterface {
    bool public constant isGoodLottery = true;

    IERC20 public token;
    uint32 public goodId;
    uint256 public goodValue;
    uint256 public publishTime;
    uint256 public lockedTime;
    string public goodHash;
    uint256 public maintenanceFee;


    mapping(address => UserRecord) public userMapping;

    mapping(address => uint[]) public userJoinRecordIndex;

    UserJoinRecord[] public userJoinRecords;

    uint public joinedUsers = 0;

    address[] public tickets;
    uint[] public ticketNums;

    address public winner;
    uint public paybackFee;
    WinnerTime public winnerTime;

    ComptrollerInterface comptroller;


    function getTokenExchangeRate() public view returns (uint256) {
        return 10**IERC20Metadata(address(token)).decimals();
    }

    function getTickets() public view returns (address[] memory) {
        address[] memory tk = tickets;
        return tk;
    }

    function getUserJoinRecords() public view returns (UserJoinRecord[] memory record) {
        return userJoinRecords;
    }

    function getUserJoinRecordIndexs(address user) public view returns (uint[] memory record) {
        return userJoinRecordIndex[user];
    }

}
