// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

import "./Exponential.sol";
import "./ComptrollerInterface.sol";

contract GoodLottery is Ownable, Exponential {
    bool public constant isGoodLottery = true;

    enum Error {
        NO_ERROR,
        INVALID_PARAMETER,
        INVALID_STATUS
    }

    enum Stage {
        NONE,
        INIT,
        CAN_JOIN,
        LOCKED,
        IN_PICK,
        FINISHED,
        FAILED
    }

    struct UserRecord {
        address user;
        uint256 count;
        bool isJoined;
        uint joinTime;
        uint joinBlockNum;
    }

    struct WinnerTime {
        uint blockNum;
        uint blockTime;
        uint index;
    }

    event JoinSuccessful(address user, uint256 amount, uint32 goodId);
    event LotteryComplete(
        address user,
        uint256 totalBalance,
        uint256 priceValue,
        uint256 mf,
        uint256 burned,
        uint256 paybackUnit
    );

    IERC20 public token;
    uint32 public goodId;
    uint256 public goodValue;
    uint256 public publishTime;
    uint256 public lockedTime;
    Stage public stage = Stage.NONE;

    uint256 public maintenanceFee;

    address[] public userList;
    mapping(address => UserRecord) public userMapping;
    address[] public tickets;
    uint[] public ticketNums;

    address public winner;
    WinnerTime public winnerTime;
    uint public paybackFee;

    ComptrollerInterface comptroller;

    string public goodHash;

    constructor(
        ComptrollerInterface _comptroller,
        IERC20 _token,
        uint32 _goodId,
        uint256 _goodValue,
        uint256 _maintenanceFee,
        uint256 _publishTime,
        uint256 _lockedTime,
        string memory _goodHash
    ) Ownable() {
        initialize(
            _comptroller,
            _token,
            _goodId,
            _goodValue,
            _maintenanceFee,
            _publishTime,
            _lockedTime,
            _goodHash
        );
    }

    function initialize(
        ComptrollerInterface _comptroller,
        IERC20 _token,
        uint32 _goodId,
        uint256 _goodValue,
        uint256 _maintenanceFee,
        uint256 _publishTime,
        uint256 _lockedTime,
        string memory _goodHash
    ) internal {
        require(stage == Stage.NONE, "good had been initialized.");
        require(
            _goodValue > 0 && _goodValue < 10000,
            "goods value must between 1 and 9999"
        );
        goodId = _goodId;
        goodValue = _goodValue;
        publishTime = _publishTime;
        lockedTime = _lockedTime;
        token = _token;
        comptroller = _comptroller;
        stage = Stage.INIT;
        maintenanceFee = _maintenanceFee;
        goodHash = _goodHash;
    }

    function setLockedTime(uint256 _lockedTime) public onlyOwner {
        lockedTime = _lockedTime;
    }

    function join(uint256 amount) public {
        Error e = joinInternal(msg.sender, amount);
        require(e == Error.NO_ERROR, "join failed");
    }

    function join(address user, uint256 amount) external {
        Error e = joinInternal(user, amount);
        require(e == Error.NO_ERROR, "join failed ");
    }

    function joinInternal(address user, uint256 amount)
        internal
        returns (Error)
    {
        uint256 currentTime = block.timestamp;

        if (
            stage == Stage.INIT &&
            currentTime > publishTime + (1 minutes) &&
            currentTime < lockedTime
        ) {
            stage = Stage.CAN_JOIN;
        }

        if (stage != Stage.CAN_JOIN) {
            return Error.INVALID_STATUS;
        }

        if (amount <= 0) {
            return Error.INVALID_PARAMETER;
        }

        uint256 decimal = 10**IERC20Metadata(address(token)).decimals();

        token.transferFrom(user, address(this), mul_(decimal, amount));

        for (uint256 i = 0; i < amount; i++) {
            tickets.push(user);
            ticketNums.push(block.number);
        }

        if (!userMapping[user].isJoined) {
            userMapping[user] = UserRecord({
                user: user,
                count: 0,
                isJoined: true, 
                joinTime: block.timestamp,
                joinBlockNum: block.number
            });
            userList.push(user);
        }

        userMapping[user].count = add_(userMapping[user].count, amount);

        emit JoinSuccessful(user, amount, goodId);

        return Error.NO_ERROR;
    }

    function pickWinner() public {
        require(address(comptroller) != address(0), "comptroller is not empty");
        require(
            stage != Stage.FINISHED && stage != Stage.FAILED,
            "pick winner had finished"
        );
        require(
            block.timestamp >= lockedTime + 10 minutes,
            "can not pick winner"
        );
        if (tickets.length == 0) {
            stage = Stage.FAILED;
            emit LotteryComplete(winner, goodValue, 0, 0, 0, 0);
            return;
        }

        stage = Stage.IN_PICK;

        uint256 index = random() % tickets.length;

        winner = tickets[index];

        comptroller.pickWinnerAllowed(address(this));

        uint256 balance = token.balanceOf(address(this));
        uint256 decimal = 10**IERC20Metadata(address(token)).decimals();
        uint256 winnerValue = mul_(goodValue, decimal);

        uint256 mf = 0;
        uint256 paybackUnit = 0;
        token.transfer(winner, winnerValue);
        (mf, paybackUnit) = paybackBalance();

        winnerTime = WinnerTime({
            blockNum: block.number,
            blockTime: block.timestamp, 
            index: index
            });

        stage = Stage.FINISHED;
        paybackFee = paybackUnit;

        emit LotteryComplete(winner, goodValue, balance, mf, 0, paybackUnit);
    }

    function paybackBalance() internal returns (uint256, uint256) {
        uint256 balance = token.balanceOf(address(this));
        if (balance <= 0) {
            return (0, 0);
        }
        uint256 mf = mul_(maintenanceFee, getTokenExchangeRate());
        if (balance <= mf) {
            token.transfer(owner(), mf);
            return (balance, 0);
        }

        uint256 needPaybakcBalance = balance - mf;
        uint256 winnerCount = tickets.length - userMapping[winner].count;
        if(winnerCount > 0) {
            uint paybackUnit = div_(needPaybakcBalance, winnerCount);

            for (uint256 i = 0; i < userList.length; i++) {
                if (userList[i] == winner) {
                    continue;
                }

                token.transfer(
                    userList[i],
                    mul_(userMapping[userList[i]].count, paybackUnit)
                );
            }
            balance = token.balanceOf(address(this));
            token.transfer(owner(), balance);
            return (balance, paybackUnit);
        } else {
            token.transfer(owner(), mf);
            balance = token.balanceOf(address(this));
            token.transfer(winner, balance);
            return (mf, balance);
        }

    }

    function random() internal view returns (uint256) {
        return
            uint256(
                keccak256(
                    abi.encodePacked(block.difficulty, block.timestamp, tickets)
                )
            );
    }

    function getTokenExchangeRate() public view returns (uint256) {
        return 10**IERC20Metadata(address(token)).decimals();
    }

    function getUsers() public view returns (address[] memory) {
        address[] memory us = userList;
        return us;
    }

    function getTickets() public view returns (address[] memory) {
        address[] memory tk = tickets;
        return tk;
    }

}
